mapper.select:
SELECT order_item_id, product_id, product_quantity
FROM order_item
WHERE client_id = #{clientId} AND pack_state = #{packState}

SELECT product_id, product_name, product_price
FROM product 
WHERE product_id = #{product_id}

mapper.update:
UPDATE order_item
SET order_item_price = #{orderItemPrice}
WHERE order_item_id = #{orderItemId}

mapper.select:
SELECT SUM(order_item_price)
FROM order_item
WHERE client_id = #{clientId} AND pack_state = #{packState}
